import * as Tone from "tone";

/**
 * Creates and handles Box object data and
 * DOM interactions, including visual callbacks
 */
export class Box {
  /**
   * @param {string} note 
   */
  constructor(note) {
    /**
     * @type {number}
     */
    this.note = note;
    /**
     * @type {HTMLElement}
     */
    this.domObject = document.createElement("span");
    /**
     * @type {boolean}
     */
    this.filled = this.setFilledStatus();
    this.domObject.classList.add("box");
  }

  /**
   * Determine whether the current box has an event associated with it
   * and sets the appropriate css class
   */
  setFilledStatus() {
    let filled = false;
    if (this.note != null) {
      filled = true;
    }
    this.domObject.classList.add(filled ? "filled-box" : "empty-box");
    return filled;
  }

  /**
   * `Box.flash()` is called by a `Row` object to animate 
   * a block as part of the `Tone.Sequencer` callback
   */
  flash() {
    var box = this.domObject;
    $(box).addClass("active-box");
    $(box).removeClass("filled-box");
    $(box).addClass("filled-box", 500, "swing");
    $(box).removeClass("active-box", 500, "swing");
  }
}

import { Tone } from 'tone/build/esm/core/Tone';
import { Loop } from './Loop.js';
import { Row } from './Row.js';

/**
 * Creates and controls the drum loop, overwrites the convertPattern() method
 */
export class DrumLoop extends Loop {
  /**
   * @param {string} tagId 
   * @param {object} data 
   * @param {Tone.Sampler} source 
   */
  constructor(tagId, data, source) {
    super(tagId, data, source);
  }

  makeRows() {
    var names = ["Kick drum", "Snare drum", "Hi hat", "Cowbell", "Clave", "L bongo", "H bongo", "Bell", "Tom"];
    for (let i = 0; i < this.parts.length; i++) {
      let row = new Row(this.source, this.parts[i], this.scale[i], names[i]);
      this.domObject.appendChild(row.domObject);
      this.rows.push(row);
    }
  }
}

import { Tone } from "tone/build/esm/core/Tone";

/**
 * Create and handle effects user interaction objects
 */
export class EffectsUI {
  /**
   * @param {string} tagId 
   * @param {*} effect1 
   * @param {*} effect2 
   */
  constructor(tagId, effect1 = null, effect2 = null) {
    /**
     * @type {string}
     */
    this.tagId = tagId;
    /**
     * @type {*}
     */
    this.effect1 = effect1;
    /**
     * @type {*}
     */
    this.effect2 = effect2;
    /**
     * @type {HTMLElement}
     */
    this.domObject = document.getElementById(this.tagId);

    this.createHandle();
  }

  /**
   * Create handle for user interactions
   */
  createHandle() {
    var handle = document.createElement("span");
    handle.classList.add("handle");
    this.domObject.appendChild(handle);

    var parent = $(handle).parent();
    var parentWidth = parent.width();
    var parentHeight = parent.height();

    var handle = $(handle); // switch to jQuery
    var handleWidth = handle.outerWidth();
    var handleHeight = handle.outerHeight();

    handle.offset({ 
      left: parent.offset().left + parentWidth / 2 - handleWidth / 2,
      top: parent.offset().top + parentHeight / 2 - handleHeight / 2
    })

    var self = this;
    handle.draggable({ 
      containment: "parent",
      drag: function() {
        var handleOffset = handle.offset();
        var left = (handleOffset.left - parent.offset().left) / parentWidth;
        var top = (handleOffset.top - parent.offset().top) / parentHeight;
        self.effect1Value = left;
        self.effect2Value = top;
        self.updateEffects();
      }
    });
  }

  /**
   * Read handle location and update effect levels
   */
  updateEffects() {
    if (this.effect1) {
      this.effect1.wet.value = this.effect1Value;
    }
    if (this.effect2) {
      this.effect2.wet.value = this.effect2Value;
    }
  }
}

import { Tone } from 'tone/build/esm/core/Tone';
import { Row } from './Row.js';

/**
 * Parent interface for MelodyLoop, BassLoop, and DrumLoop
 */
export class Loop {
  /**
   * Synthesizer loop structure and graphical representation
   * @param {string} tagId 
   * @param {object} data 
   * @param {Tone.Synth} source 
   */
  constructor(tagId, data, source) {
    /**
     * @type {string}
     */
    this.tagId = tagId;
    /**
     * @type {Tone.synth}
     */
    this.source = source;
    /**
     * @type {Array}
     */
    this.parts = data.parts;
    /**
     * @type {number}
     */
    this.tempo = data.tempo;
    /**
     * @type {Array}
     */
    this.rows = [];
    /**
     * @type {HTMLElement}
     */
    this.domObject = document.getElementById(tagId);

    /**
     * @type {Array}
     */
    this.scale = this.setScale(data.scale);
    // if (data.names) { this.names = data.names }
    this.convertPattern();
    this.makeRows();
  }

  /**
   * @return {Array} Scale members as strings with note names and octaves
   */
  setScale(scale) {
    switch (scale) {
      case "dorianMelody": return ["C6", "Bb5", "A5", "G5", "F5", "Eb5", "D5", "C5", "Bb4", "A4", "G4", "F4", "Eb4", "D4", "C4"];
      case "dorianBass": return ["C4", "Bb3", "A3", "G3", "F3", "Eb3", "D3", "C3", "Bb2", "A2", "G2", "F2", "Eb2", "D2", "C2"];
      case "drumSet": return ["C3", "D3", "E3", "F3", "G3", "A3", "B3", "C4", "D4"];
    }
    throw "Unknown scale";
  }

  /**
   * Convert an array of note names into a series of patterns for use by `makeRows()`
   */
  convertPattern() {
    var newPartsArray = [];

    for (let i = 0; i < this.scale.length; i++) {
      let currentNote = this.scale[i];
      let currentPart = this.parts[i];
      let convertedPart = [];
      currentPart.forEach(note => {
        if (note == 1) { convertedPart.push(currentNote); }
        else { convertedPart.push(null) }
      });
      newPartsArray.push(convertedPart);
    }
    this.parts = newPartsArray;
  }

  makeRows() {
    for (let i = 0; i < this.parts.length; i++) {
      let row = new Row(this.source, this.parts[i], this.scale[i]);
      this.domObject.appendChild(row.domObject);
      this.rows.push(row);
    }
  }
}

import * as Tone from "tone";

/**
 * Create master play button to start Tone.js and control play/pause events
 */
export class PlayButton {
  /**
   * Get element and create play button
   */
  constructor() {
    /**
     * @type {HTMLElement}
     */
    this.domObject = document.getElementById("playButton");
    this.domObject.innerText = "Start";
    this.domObject.start = this.start;
    this.domObject.stop = this.stop;
    this.domObject.addEventListener("click", this.listener.bind(this));
    document.addEventListener("keydown", e => {
      if (e.code === "Space") {
        e.preventDefault();
        this.listener();
      }
    });
  }

  /**
   * Activate Tone.js and control start/stop functionality
   */
  async listener() { 
    if (this.domObject.innerText == "Start") {
      await Tone.start();
      this.start();
    } else {
      this.stop();
    }
  }

  /**
   * Start Tone.Transport
   */
  start() {
    this.domObject.innerText = "Stop";
    Tone.Transport.start("+0.01")
  }

  /**
   * Stop Tone.Transport
   */
  stop() {
    Tone.Transport.stop();
    this.domObject.innerText = "Start";
  }
}

import * as Tone from 'tone';
import { Box } from './Box.js';

/**
 * Creates and controls DOM objects for individual loop rows
 */
export class Row {
  /**
   * @param {object} part
   * @param {string} name
   * @param {string} note
   * @param {Tone.Synth} source 
   */
  constructor(source, pattern, note, name = note) {
    /**
     * @type {number}
     */
    this.labelWidth = 6.0;
    /**
     * @type {string}
     */
    this.name = name;
    /**
     * @type {array}
     */
    this.note = note;
    /**
     * @type {array}
     */
    this.pattern = pattern;
    /**
    * @type {Tone.synth}
    */
    this.source = source;
    this.createLoop();
    /**
     * @type {HTMLElement}
     */
    this.domObject = document.createElement("div");
    this.domObject.classList.add("row-of-boxes");
    this.domObject.appendChild(this.makeRowLabel(this.name));
    /**
     * @type {array}
     */
    this.boxes = this.createBoxes();
  }

  /**
   * 
   * @param {string} label Name of the row, to be added to the DOM object before any boxes
   * @return {HTMLSpanElement} HTML span element with the row name
   */
  makeRowLabel(label) {
    var rowName = document.createElement("span");
    rowName.classList.add("row-label");
    rowName.innerText = label;
    rowName.style.width = this.labelWidth + "em";
    return rowName;
  }

  /**
   * Build and store the series of boxes in the row
   */
  createBoxes() {
    var boxes = [];
    let widthString = this.calculateBoxWidth();
    for (let i = 0; i < this.pattern.length; i++) {
      let box = new Box(this.pattern[i]);
      box.domObject.style.width = widthString;

      // add event listeners
      box.domObject.addEventListener("click", () => {
        this.switchFilledStatus(box);
        this.updateSequencePattern(i, box.filled);
        this.createLoop();
      })

      // display and store the box
      this.domObject.appendChild(box.domObject);
      boxes.push(box);
    };
    return boxes;
  }

  /**
   * Called by `createBoxes`, uses the number of boxes in
   * a row to calculate and set the CSS width property
   */
  calculateBoxWidth() {
    let percent = 100.0 / this.pattern.length + "%";
    let padding = this.labelWidth / this.pattern.length + 0.1;
    return "calc(" + percent + " - " + padding + "em)";
  }

  /**
   * Used by the constructor and click event callbacks to remove the
   * previous loop and replace it with an updated version
   */
  createLoop() {
    // clear the previous sequence if one exists
    if (this.sequence) {
      this.sequence.dispose();
    }

    // create the new sequence for this row
    this.sequence = new Tone.Sequence((time, note) => {
      this.flashActiveBox();
      this.source.triggerAttackRelease(note, "8n", time);
    }, this.pattern).start(0);
  }

  /**
   * Called in the Tone.Sequencer callback to determine which
   * box to animate
   */
  flashActiveBox() {
    var [beat, sixteenth] = Tone.Transport.position.split(/\.|:/).slice(1,3);
    beat = Number(beat);
    sixteenth = Number(sixteenth);
    var currentPosition = beat * 2 + sixteenth / 2, beat, sixteenth;
    if (this.boxes[currentPosition].filled) {
      this.boxes[currentPosition].flash();
    }
  }

  /**
   * Switch the status of a box as filled and not filled
   * @param {Box} box
   */
  switchFilledStatus(box) {
    var filled = false;
    if (box.domObject.classList.contains("filled-box")) {
      box.domObject.classList.remove("filled-box");
      box.domObject.classList.add("empty-box");
    } else {
      box.domObject.classList.add("filled-box");
      box.domObject.classList.remove("empty-box");
      filled = true;
    }
    box.filled = filled;
  }

  /**
   * Update the sequence object passed by Row whenever a user clicks
   * a box. This is part of the Box click event listener
   */
  updateSequencePattern(positionNumber, filled) {
    var pattern = this.sequence._eventsArray;
    if (filled) {
      pattern[positionNumber] = this.note;
    } else {
      pattern[positionNumber] = null;
    }
    this.pattern = pattern;
  }
}

import * as Tone from 'tone';

import hihat from "../audio/hihat.mp3";
import snare from "../audio/snare.mp3";
import kick from "../audio/kick.mp3";
import clave from "../audio/clave.mp3";
import ding from "../audio/ding.mp3";
import bongo1 from "../audio/bongo1.mp3";
import bongo2 from "../audio/bongo2.mp3";
import tom3 from "../audio/tom3.mp3";
import cowbell from "../audio/cowbell.mp3";

export function createDrumSampler() {
  const drumSampler = new Tone.Sampler({
    urls: {
      C3: kick,
      D3: snare,
      E3: hihat,
      F3: cowbell,
      G3: clave,
      A3: bongo1,
      B3: bongo2,
      C4: ding,
      D4: tom3,
    },
  }).toDestination();

  return drumSampler;
}

export function createSynth() {
  const synth = new Tone.PolySynth().toDestination();
  return synth;
}

import * as Tone from 'tone';
import { Loop } from './Classes/Loop.js';
import { DrumLoop } from './Classes/DrumLoop.js';
import { PlayButton } from './Classes/PlayButton.js';
import { EffectsUI } from './Classes/EffectsUI.js';
import { loops } from './loops.js';
import { createDrumSampler, createSynth } from './audio.js';

const drumSampler = createDrumSampler();
const drumVolumeNode = new Tone.Volume(-6).toDestination();
drumSampler.connect(drumVolumeNode);

const melodySynth = createSynth();
const melodyVolumeNode = new Tone.Volume(0).toDestination();
melodySynth.connect(melodyVolumeNode);

const bassSynth = createSynth();
const bassVolumeNode = new Tone.Volume(-20).toDestination();
bassSynth.connect(bassVolumeNode);

// add effects
const melodyEffect1 = new Tone.Distortion({
  distortion: 10,
  wet: 0.5,
})

const melodyEffect2 = new Tone.FeedbackDelay({
  delayTime: "8n",
  feedback: 0.7,
  maxDelay: "4n",
  wet: 0.5
}).toDestination();

melodySynth.chain(melodyEffect1, melodyEffect2);

const bassEffect1 = new Tone.BitCrusher({ 
  bits: 2,
  wet: 0.5
}).toDestination();

const bassEffect2 = new Tone.Chorus({
  depth: 1,
  delayTime: 700,
  feedback: 0.4,
  wet: 0.5,
}).toDestination();

bassSynth.chain(bassEffect1, bassEffect2);

// set up loops
const Play = new PlayButton();
const melodyLoop = new Loop("melodyLoop", loops.melodyLoop, melodySynth);
const bassLoop = new Loop("bassLoop", loops.bassLoop, bassSynth);
const drumLoop = new DrumLoop("drumLoop", loops.drumLoop, drumSampler);

// set up filter listeners
const synthEffects = new EffectsUI("melodyEffects", melodyEffect1, melodyEffect2);
const bassEffects = new EffectsUI("bassEffects", bassEffect1, bassEffect2);

