export class Box {
  constructor(positionNumber, value) {
    this.value = value;
    this.positionNumber = positionNumber;
    this.position = this.getTransportPosition(this.positionNumber);
    this.domObject = document.createElement("span");
    this.filled = this.isFilled();
    this.domObject.classList.add("box");
  }

  /**
   * Called by the parent `Row` using the number of boxes
   * to calculate and set the CSS width property
   * @param {number} numberOfBoxes - The number of boxes in the row
   * @param {number} labelWidth - The row name with, to subtract from the calculation
   */
  calculateWidth(numberOfBoxes, labelWidth) {
    let percent = 100.0 / numberOfBoxes + "%";
    let padding = labelWidth / numberOfBoxes + 0.1;
    this.domObject.style.width = "calc(" + percent + " - " + padding + "em)";
  }

  /**
   * Used to calculate the position for Tone.Transport
   * @param {number} positionNumber - This box's place in the row 
   */
  getTransportPosition(positionNumber) {
    var beat = Math.floor(positionNumber / 2);
    var sixteenth = positionNumber % 2;
    return `${beat}:${sixteenth}`;
  }

  /**
   * Determine whether the current box has an event associated with it
   * and sets the appropriate css class
   */
  isFilled() {
    let filled = false;
    if (this.value != null && this.value != 0) {
      filled = true;
    }
    this.domObject.classList.add(filled ? "filled-box" : "empty-box");
    return filled;
  }

  /**
   * `Box.flash()` is called by a `Row` object to animate 
   * a block as part of the `Tone.Sequencer` callback
   */
  flash() {
    var box = this.domObject;
    var bgColor = window.getComputedStyle(box, null).getPropertyValue("background-color");
    box.style.backgroundColor = "#0a4f2d";
    setTimeout(function () {
      box.animate({ backgroundColor: bgColor }, 1000);
    });
    setTimeout(function () {
      box.style.backgroundColor = bgColor;
    }, 1000);
  }
}

import { Loop } from './Loop.js';

export class DrumLoop extends Loop {
  constructor(tagId, data, source) {
    super(tagId, data, source);
  }

  /**
   * Create loops, overwrites `Loop.setUpLoop()` to convert
   * an array of numbers into notes, as used by the drum sampler
   * @return { Array } An array of sequence objects
   */
  setUpLoop() {
    var sequences = [];
    for (let i = 0; i < this.parts.length; i++) {
      this.parts[i] = this.convertPatternToNotes(this.parts[i]);
      sequences.push(this.createLoop(i));
    }

    return sequences;
  }

  /**
   * Used by the overwritten setUpLoop() to convert an array
   * of numbers into note names
   * @param { Object } part Part object that includes an array of numbers in `part.pattern`
   * @return { Object } Part object with the pattern converted into notes
   */
  convertPatternToNotes(part) {
    for (let i = 0; i < part.pattern.length; i++) {
      if (part.pattern[i] == 0) {
        part.pattern[i] = null
      } else {
        part.pattern[i] = part.note;
      }
    }
    return part;
  }
}

export class EffectsUI {
  constructor(tagId, effect1 = null, effect2 = null) {
    this.tagId = tagId;
    this.effect1 = effect1;
    this.effect2 = effect2;
    this.domObject = document.getElementById(this.tagId);

    this.createHandle();
  }

  createHandle() {
    var handle = document.createElement("span");
    handle.classList.add("handle");
    this.domObject.appendChild(handle);

    var parent = $(handle).parent();
    var parentWidth = parent.width();
    var parentHeight = parent.height();

    var handle = $(handle); // switch to jQuery
    var handleWidth = handle.outerWidth();
    var handleHeight = handle.outerHeight();

    handle.offset({ 
      left: parent.offset().left + parentWidth / 2 - handleWidth / 2,
      top: parent.offset().top + parentHeight / 2 - handleHeight / 2
    })

    var self = this;
    handle.draggable({ 
      containment: "parent",
      drag: function() {
        var handleOffset = handle.offset();
        var left = (handleOffset.left - parent.offset().left) / parentWidth;
        var top = (handleOffset.top - parent.offset().top) / parentHeight;
        self.effect1Value = left;
        self.effect2Value = top;
        self.updateEffects();
      }
    });
  }

  updateEffects() {
    if (this.effect1) {
      this.effect1.wet.value = this.effect1Value;
    }
    if (this.effect2) {
      this.effect2.wet.value = this.effect2Value;
    }
  }
}

import * as Tone from 'tone';
import { Timeline } from 'tone';
import { Row } from './Row.js';

export class Loop {
  /**
   * Synthesizer loop structure and graphical representation
   * @param {string} tagId 
   * @param {object} data 
   * @param {Tone.Synth} source 
   */
  constructor(tagId, data, source) {
    /**
     * @type {string}
     */
    this.tagId = tagId;
    /**
     * @type {Tone.synth}
     */
    this.source = source;
    /**
     * @type {Array}
     */
    this.parts = data.parts;
    /**
     * @type {number}
     */
    this.tempo = data.tempo;
    /**
     * @type {Array}
     */
    this.rows = [];
    /**
     * @type {HTMLElement}
     */
    this.domObject = document.getElementById(tagId);
    this.makeRows();
    /**
     * @type {Array}
     */
    this.sequences = this.setUpLoop(source);
  }

  makeRows() {
    var rows = 0;
    for (let i = 0; i < this.parts.length; i++) {
      let row = new Row(this.parts[i]);
      this.domObject.appendChild(row.domObject);
      this.rows.push(row);
      rows++;
    }
  }

  setUpLoop() {
    var sequences = [];
    for (let i = 0; i < this.parts.length; i++) {
      sequences.push(this.createLoop(i));
    }
    return sequences;
  }

  createLoop(partNumber) {
    var sequence = new Tone.Sequence((time, note) => {
      this.rows[partNumber].flashActiveBox();
      this.source.triggerAttackRelease(note, "8n", time);
    }, this.parts[partNumber].pattern).start(0);
    return sequence;
  }
}

import * as Tone from "tone";

export class PlayButton {
  constructor() {
    this.domObject = document.getElementById("playButton");
    this.domObject.innerText = "Start";

    this.domObject.start = this.start;
    this.domObject.stop = this.stop;
    this.domObject.addEventListener("click", this.listener.bind(this));
    document.addEventListener("keydown", e => {
      if (e.code === "Space") {
        e.preventDefault();
        this.listener();
      }
    });
  }

  async listener(e) { 
    if (this.domObject.innerText == "Start") {
      await Tone.start();
      this.start();
    } else {
      this.stop();
    }
  }

  start() {
    this.domObject.innerText = "Stop";
    Tone.Transport.start("+0.01")
  }

  stop() {
    Tone.Transport.pause();
    this.domObject.innerText = "Start";
  }
}

import { Box } from './Box.js';

export class Row {
  constructor(part) {
    this.labelWidth = 6.0;
    this.name = part.name;
    this.boxes = [];
    this.domObject = document.createElement("div");
    this.domObject.classList.add("row-of-boxes");
    this.domObject.appendChild(this.makeRowLabel(part.name));

    for (let i = 0; i < part.pattern.length; i++) {
      let box = new Box(i, part.pattern[i]);
      box.calculateWidth(part.pattern.length, this.labelWidth);
      this.domObject.appendChild(box.domObject);
      this.boxes.push(box);
    }
  }

  makeRowLabel(label) {
    var rowName = document.createElement("span");
    rowName.classList.add("row-label");
    rowName.innerText = label;
    rowName.style.width = this.labelWidth + "em";
    return rowName;
  }

  flashActiveBox() {
    var filledBoxes = this.boxes.filter(box => box.domObject.classList.contains("filled-box"));

    var activeBoxIndex = 0;
    for (let i = 0; i < filledBoxes.length; i++) {
      if (filledBoxes[i].domObject.classList.contains("active-box")) {
        filledBoxes[i].domObject.classList.remove("active-box");
        activeBoxIndex = (i + 1) % filledBoxes.length;
        break;
      }
    }

    var activeBox = filledBoxes[activeBoxIndex];
    activeBox.domObject.classList.add("active-box");
    activeBox.flash();
  }
}

