export function createDrumSampler() {
  const drumSampler = new Tone.Sampler({
    urls: {
      A1: hihat,
      A2: snare,
      A3: kick,
      A4: clave,
      A5: ding,
      A6: bongo1,
      A7: bongo2,
      A8: tom3,
      A9: cowbell,
    },
  }).toDestination();

  return drumSampler;
}

export function createSynth() {
  const synth = new Tone.Synth().toDestination();
  return synth;
}

